                long_return = 0L;
                /* oha_pdu_get_u16 (long_return, NamePtr);  */
                long_return = (LSA_INT32)OHA_GET16_NTOH (NamePtr, 0); /* 2nd word */


                /* Bit 3 - 14: RTClass3_PortStatus.reserved */
