			if ((oha_snmp_compare(current, 15+1, name, *length) > 0) &&
				(!low_if || (oha_snmp_compare(current, 15+1, lowest, 15+1) < 0)))
			{
				OHA_MEMCPY((char *)lowest, (char *)current, (LSA_UINT32)(vp->namelen+1) * sizeof(OHA_OID));
				low_if = inter_face;
				low_port = port_id;
			}
